  constructor(private localStorageService: LocalStorageService) { }

  ngOnInit() {
    let getObj = this.localStorageService.get("KRUK");
    if (getObj) {
      this.KRUK = JSON.parse(JSON.stringify(this.localStorageService.get("KRUK")));
    }
    this.doubleArray = this.buildArr(this.KRUK);
    this.current = this.KRUK[0];
  }

  buildArr(theArr) {
    let arrOfarr = [];
    for(var i = 0; i < theArr.length ; i+=this.step) {
        var row = [];
        for(var x = 0; x < this.step; x++) {
          var value = theArr[i + x];
          if (!value) {
              break;
          }
          value.ind = i + x;
          row.push(value);
        }
        arrOfarr.push(row);
    }
    return arrOfarr;
  }

  changeStep(newStep) {
    if (isNaN(newStep)) return;
    let newVal = Number(newStep);
    if (newVal >= 1 && newVal <= 15) {
      this.step = newVal;
      this.doubleArray = this.buildArr(this.KRUK);
    }
  }

  add() {
    this.current.clazz = "";
    let newObj = JSON.parse(JSON.stringify(this.current));
    newObj.ind = this.KRUK.length;
    this.KRUK.push(newObj);
    this.current = newObj;
    this.doubleArray = this.buildArr(this.KRUK);
    this.current.clazz = "highlight";
    this.save();
  }

  save() {
    this.localStorageService.set("KRUK", this.KRUK.map((x) => {return {value: x.value, pitch: x.pitch, name: x.name, sounds: x.sounds, opts: x.opts}} ));
  }

  changeIndex(oldVal, newVal) {
    if (newVal >= 0 && newVal <= this.KRUK.length-1 && oldVal!=newVal) {
      this.KRUK.splice(newVal, 0, this.KRUK.splice(oldVal, 1)[0]);
      this.doubleArray = this.buildArr(this.KRUK);
      this.current.ind = newVal;
      this.save();
    }
  }

  export() {
    this.save();
    this.parsedKRUK = JSON.stringify(this.localStorageService.get("KRUK"));
    this.areaVisible = true;
  }

  selectCurrent(item) {
    this.current.clazz = "";
    this.current = item;
    this.current.clazz = "highlight";
    this.save();
  }


  hoverIn(item) {
    item.clazz = "highlight";
  }

  hoverOut(item) {
    if (item.ind != this.current.ind){
      item.clazz = "";
    }
  }



